sect3.para:
	  To parse any number of functions in a file, do :

  #include <execution/Parser.hpp>

  TaskContext* a_task = ...
  Parser parser;

  std::vector<FunctionGraph*> result =
      parser.parseFunction( "Functions.ops", a_task );

  // if needed, also initialise the arguments of 
  // the FunctionGraph
  a_task->getProcessor()->executeFunction( result[0] );

  while( !result[0]->isFinished() && !result[0]->inError() )
     sleep(1);

  delete result[0];

para.programlisting:
#include <execution/Parser.hpp>

TaskContext* a_task = ...
Parser parser;

std::vector<ProgramGraph*> result =
    parser.parseProgram( "ProgramBar.ops", a_task );

// load a program
a_task->getProcessor()->loadProgram( result[0] );
// start a program
a_task->getProcessor()->startProgram( result[0] );

#include <execution/Parser.hpp>

TaskContext* a_task = ...
Parser parser;

std::vector<ParsedStateMachine*> result =
    parser.parseStateMachine( "StateMachineBar.osd", a_task );

// load a state context
a_task->getProcessor()->loadStateMachine( result[0] );
// enter the initial state
a_task->getProcessor()->activateStateMachine( result[0]->getName() );
// start state-transition evaluation
a_task->getProcessor()->startStateMachine( result[0]->getName() );

