sect2.programlisting:
  #include <execution/TaskContext.hpp>
  #include <corelib/Event.hpp>

  class MyTask : public TaskContext
  {
     ORO_CoreLib::Event< void( int ) > myEvent;
     int data;
  public:
     MyTask()
	: TaskContext("myTask"),
	  myEvent("theEvent"), data(0)
     {}

     // ...
     void foo() 
     {
        myEvent.fire( data );
     }
  };

  #include <execution/TaskContext.hpp>
  #include <corelib/Event.hpp>
  #include <boost/bind.hpp>

  class MyOtherTask : public TaskContext
  {
     ORO_CoreLib::TaskPreemptible ptask;
     int inputdata;
  public:
     MyOtherTask()
	: TaskContext("myTask"),
          ptask( 0.01, this->getProcessor() ),
	  inputdata(0)
     {
     }

     void setUpEvent()
     {
        assert( proc->getTask() != 0 );

	// connect bar with 'theEvent'
        ORO_CoreLib::Event< void (int) >::nameserver.getObject("theEvent")
           ->connect( boost::bind( &MyOtherTask::bar, this), 
                      proc->getTask() );
     }

     // The event handler :
     void bar( int in ) 
     {
	this->inputdata = in;
     }
  };

